import i18next from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

// Import all translations
import enCommon from './en/common.json'; // Adjusted path
import enDashboard from './en/dashboard.json'; // Adjusted path
import enAuth from './en/auth.json'; // Adjusted path
import enReceipts from './en/receipts.json'; // Adjusted path
import enAnalytics from './en/analytics.json'; // Adjusted path
import enInventory from './en/inventory.json'; // Adjusted path
import enSettings from './en/settings.json'; // Adjusted path

import heCommon from './he/common.json';
import heDashboard from './he/dashboard.json';
import heAuth from './he/auth.json';
import heReceipts from './he/receipts.json';
import heAnalytics from './he/analytics.json';
import heInventory from './he/inventory.json';
import heSettings from './he/settings.json';

const resources = {
  en: {
    common: enCommon,
    dashboard: enDashboard,
    auth: enAuth,
    receipts: enReceipts,
    analytics: enAnalytics,
    inventory: enInventory,
    settings: enSettings
  },
  he: {
    common: heCommon,
    dashboard: heDashboard,
    auth: heAuth,
    receipts: heReceipts,
    analytics: heAnalytics,
    inventory: heInventory,
    settings: heSettings
  }
};

i18next
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    resources,
    fallbackLng: 'en',
    debug: process.env.NODE_ENV === 'development',
    interpolation: {
      escapeValue: false,
    },
    detection: {
      order: ['localStorage', 'navigator'],
      caches: ['localStorage'],
      lookupLocalStorage: 'i18nextLng'
    }
  });

export default i18next;
