import React from 'react';

export const Button = ({
  variant = 'primary',
  size = 'md',
  disabled = false,
  type = 'button',
  icon,
  children,
}) => (
    <button
      type={type}
      className={`px-4 py-2 rounded-md font-medium transition-colors ${
        disabled
          ? 'opacity-50 cursor-not-allowed'
          : 'hover:bg-primary-700 hover:text-white focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500'
      } ${
        variant === 'primary'
          ? 'bg-primary-600 text-white'
          : variant === 'secondary'
            ? 'bg-gray-200 text-gray-900 hover:bg-gray-300'
            : 'bg-white border border-gray-300 text-gray-700 hover:bg-gray-50'
      } ${size === 'sm' ? 'px-3 py-1.5 text-sm' : size === 'lg' ? 'px-6 py-2 text-lg' : ''}`}
      disabled={disabled}
    >
      {icon && <span className="mr-2">{icon}</span>}
      {children}
    </button>
  );

export default Button;
