import { createBrowserRouter } from 'react-router-dom';

// Core Pages
import NotFoundPage from './core/pages/NotFoundPage';

// Feature Pages - Use default imports to match the export style
import DashboardPage from './features/analytics/pages/DashboardPage';
import ReceiptListPage from './features/receipts/pages/ReceiptListPage';
import ReceiptDetailPage from './features/receipts/pages/ReceiptDetailPage';
import ReportsPage from './features/analytics/pages/ReportsPage';
import SettingsPage from './features/settings/pages/SettingsPage';

// Auth Components
import AuthGuard from './features/auth/components/AuthGuard';
import { ForgotPasswordPage } from './features/auth/components/ForgotPasswordPage';
import { LoginPage } from './features/auth/components/LoginPage';
import { RegisterPage } from './features/auth/components/RegisterPage';

// Shared Layout
import { Layout } from './shared/components/layout/Layout';

export const router = createBrowserRouter([
  {
    path: '/',
    element: (
      <AuthGuard>
        <Layout />
      </AuthGuard>
    ),
    errorElement: <NotFoundPage />,
    children: [
      // Main dashboard is the index route
      { index: true, element: <DashboardPage /> },
      
      // Receipt routes
      { path: 'receipts', element: <ReceiptListPage /> },
      { path: 'receipts/:receiptId', element: <ReceiptDetailPage /> },
      
      { path: 'reports', element: <ReportsPage /> },
      { path: 'settings', element: <SettingsPage /> },
      // Add other feature routes here (e.g., inventory)
    ],
  },
  // Auth routes are top-level
  { path: '/login', element: <LoginPage /> },
  { path: '/register', element: <RegisterPage /> },
  { path: '/forgot-password', element: <ForgotPasswordPage /> },
]);

export default router;
