import { initializeApp, getApps } from 'firebase/app';
import { getAuth } from 'firebase/auth';
import { getFirestore } from 'firebase/firestore';
import { getStorage } from 'firebase/storage';

// --- Diagnostic Logging Function ---
function logFirebaseStatus(message, data = {}) {
  console.log(`[Firebase] ${message}`, data);
}

// --- Hardcoded Firebase Configuration ---
// IMPORTANT: Replace with environment variables once .env issues are resolved
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
  appId: process.env.REACT_APP_FIREBASE_APP_ID,
  measurementId: process.env.REACT_APP_FIREBASE_MEASUREMENT_ID
};

// --- Core Firebase Services ---
let app;
let auth;
let db;
let storage;

// --- Initialize Firebase ---
try {
  // Check if Firebase is already initialized
  if (!getApps().length) {
    logFirebaseStatus("Initializing Firebase app...");
    app = initializeApp(firebaseConfig);
    logFirebaseStatus("Firebase app initialized successfully");
  } else {
    logFirebaseStatus("Firebase app already initialized");
    app = getApps()[0];
  }

  // Initialize services
  auth = getAuth(app);
  db = getFirestore(app);
  storage = getStorage(app);
  logFirebaseStatus("Firebase services initialized successfully", {
    services: ["Authentication", "Firestore", "Storage"]
  });
} catch (error) {
  console.error("CRITICAL ERROR: Firebase initialization failed", error);
  throw error;
}

